BEGIN TRANSACTION;
INSERT OR REPLACE INTO "datas" VALUES (81332143,3,0,0,2,0,0,0,0,0,0);
INSERT OR REPLACE INTO "texts" VALUES (81332143, 'Yu-Jo Friendship', 'Offer your opponent a handshake. If they accept your handshake, each player''s Life Points become half the combined Life Points of both players. If you have "Unity" in your hand and show it to your opponent, they must accept the handshake.', 'Accept handshake?', 'Do you want to reveal "Unity"?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT OR REPLACE INTO "datas" VALUES (55948544,3,0,0,4,0,0,0,0,0,0);
INSERT OR REPLACE INTO "texts" VALUES (55948544, 'Judgment of the Pharaoh', 'Pay half your Life Points. Select and activate an appropriate effect which can be applied:
● If "Yu-Jo Friendship" is in your Graveyard, until the end of this turn: your opponent cannot Normal Summon, Set, Flip Summon, or Special Summon a monster(s), or activate an Effect Monster''s effect, and their monster''s effects are negated.
● If "Unity" is in your Graveyard, until the end of this turn: negate the effects of all Spell and Trap Cards on your opponent''s side of the field, and your opponent cannot activate or Set Spell or Trap Cards.', 'Until the end of this turn: your opponent cannot Normal Summon, Set, Flip Summon, or Special Summon a monster(s), or activate an Effect Monster''s effect, and their monster''s effects are negated.', 'Until the end of this turn: negate the effects of all Spell and Trap Cards on your opponent''s side of the field, and your opponent cannot activate or Set Spell or Trap Cards.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
COMMIT;
